const initialState = [
  {
    wonBy: "",
    isDeuce: false,
    finished: false
  }
]

export default (state = initialState, action) => {
  switch (action.type) {
    case "GAME_WON":
      let games_new_state = state;
      for (let i = 0; i < games_new_state.length; i++) {
        if (i === action.game - 1) {
          games_new_state[i].wonBy = action.player;
          games_new_state[i].finished = true;
        }
      }
      return games_new_state;
  }
  return state;
};
