import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import {
  wonBall
} from '../actions/index.js';
import '../styles/App.css';

class App extends Component {
  render() {
    const { current_game, balls } = this.props;
    let player1_score, player2_score = 0;
    const possible_scores = {
      0: 0,
      1: 15,
      2: 30,
      3: 40
    };

    return (
      <div className="App">
        <h1>
          Game {current_game}
        </h1>
        <div className="game">
          <div className="player">
            <h2>
              Player 1
            </h2>
            <p>
              0
            </p>
            <button onClick={() => this.props.wonBall("player1")}>
              Won the ball!
            </button>
          </div>
          <div className="player">
            <h2>
              Player 2
            </h2>
            <p>
              0
            </p>
            <button onClick={() => this.props.wonBall("player1")}>
              Won the ball!
            </button>
          </div>
        </div>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    wonBall
  }, dispatch)
}

const selector = createSelector(
  state => state['current_game'],
  state => state['balls'],
  (
    current_game,
    balls
) => {
    return  {
      current_game,
      balls
    };
  }
);

export default connect(selector, matchDispatchToProps)(App);
