import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';

class Concept extends Component {
  render() {
    const { title, date, description, id, concepts } = this.props;
    const concepts_keys = Object.keys(concepts);
    const last_id = concepts_keys[concepts_keys.length - 1];

    return (
      <div
        style={id === last_id ? { borderBottom:"none" } : {}}
        className="concept">
        <p className="hat">
          {date.toUpperCase()}
        </p>
        <h2 className="head">
          {title}
        </h2>
        <p className="description">
          {description}
        </p>
        <div className="CTA">
          READ STORY
        </div>
      </div>
    );
  }
}

const selector = createSelector(
  state => state['concepts'],
  (
    concepts
) => {
    return  {
      concepts
    };
  }
);

export default connect(selector)(Concept);
