import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import map from 'lodash/map';
import Player from './Player.js';
import {
  wonBall
} from '../actions/index.js';
import '../styles/App.css';

class App extends Component {
  renderPlayers() {
    const { players } = this.props;

    return(
      map(
        players,
        (player, index) => <Player key={index} index={index} />
      )
    )
  }

  render() {
    const { current_game, balls } = this.props;
    const possible_scores = [0, 15, 30, 40];
    const player1_score = possible_scores[balls.player1];
    const player2_score = possible_scores[balls.player2];

    return (
      <div className="App">
        <h1>
          Game {current_game}
        </h1>
        <div className="game">
          {this.renderPlayers()}
          {/* <div className="player">
            <h2>
              Player 1
            </h2>
            <p>
              {player1_score}
            </p>
            <button onClick={() => this.props.wonBall("player1")}>
              Won the ball!
            </button>
          </div>
          <div className="player">
            <h2>
              Player 2
            </h2>
            <p>
              {player2_score}
            </p>
            <button onClick={() => this.props.wonBall("player2")}>
              Won the ball!
            </button>
          </div> */}
        </div>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    wonBall
  }, dispatch)
}

const selector = createSelector(
  state => state['current_game'],
  state => state['players'],
  state => state['balls'],
  (
    current_game,
    players,
    balls
) => {
    return  {
      current_game,
      players,
      balls
    };
  }
);

export default connect(selector, matchDispatchToProps)(App);
