const initialState = {
  1: {
    wonBy: "",
    isDeuce: false,
  }
}

export default (state = initialState, action) => {
  switch (action.type) {
    case "GAME_WON":
      return {
        ...state,
        [action.game]: {
          wonBy: action.player
        }
      }
  }
  return state;
};
