import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import {
  wonBall
} from '../actions/index.js';
import '../styles/App.css';

class Player extends Component {
  render() {
    const { current_game, balls, index } = this.props;
    const possible_scores = [0, 15, 30, 40];
    const player1_score = possible_scores[balls.player1];
    const player2_score = possible_scores[balls.player2];

    return (
      <div className="player">
        <h2>
          {index}
        </h2>
        <p>
          {player1_score}
        </p>
        <button onClick={() => this.props.wonBall("player1")}>
          Won the ball!
        </button>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    wonBall
  }, dispatch)
}

const selector = createSelector(
  state => state['balls'],
  (
    balls
) => {
    return  {
      balls
    };
  }
);

export default connect(selector, matchDispatchToProps)(Player);
